import React from 'react';
// import {connect} from 'react-redux';
// import {userLoginFetch} from '../../../actions/loginAction';
import axios from 'axios';
import '../../../styles/Login.css';
import {Link} from 'react-router-dom';

class Login extends React.Component {
  state={
    username: "",
    password: ""
  }
  handleChange = event =>{
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  handleSubmit = event =>{
    
    axios({
      method: "POST",
      url: "http://localhost:8000/auth/login",
      data: {
        username: this.state.username,
        password: this.state.password
      }
    }).then(
      response =>{
        const token= response.data.access_token
        localStorage.setItem("access_token", token)
        console.log(token)
      }
    )
  }
  render() { 
    return ( 
    <>
      <div className="login_space">
      <div className="login_page">
        <div className="form">
        <div className="ad_logo">
          <h2 className="font-italic">Ad setting</h2>
        </div>
          <form className="login_form" onSubmit={this.handleSubmit}>
            <label>User name:</label>
            <input type="text" placeholder="username" onChange={this.handleChange}/>
            <label>Password:</label>
            <input type="password" placeholder="password" onChange={this.handleChange} />
            <Link to='/homepage'>
            <button>login</button>
            </Link>

          </form>
        </div>
      </div>
      </div>
      
      
      </> );
  }
}
export default Login;
