import React from 'react';
import {connect} from 'react-redux';
import {userLoginFetch} from '../../../actions/loginAction';
import '../../../styles/Login.css';
import {Link} from 'react-router-dom';

class Login extends React.Component {
  state={
    username: "",
    password: ""
  }
  handleChange = event =>{
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  handleSubmit = event =>{
    event.preventDefailt()
    this.props.userLoginFetch(this.state)
  }
  render() { 
    return ( 
    <>
      <div className="login_space">
      <div className="login_page">
        <div className="form">
        <div className="ad_logo">
          <h2 className="font-italic">Ad setting</h2>
        </div>
          <form className="login_form" onSubmit={this.handleSubmit}>
            <label>User name:</label>
            <input type="text" placeholder="username" onChange={this.handleChange}/>
            <label>Password:</label>
            <input type="password" placeholder="password" onChange={this.handleChange} />
            <Link to='/homepage'>
            <button>login</button>
            </Link>

          </form>
        </div>
      </div>
      </div>
      
      
      </> );
  }
}
 const mapDispatchToProps = dispatch =>({
   userLoginFetch: userInfo => dispatch(userLoginFetch(userInfo))
 })
export default connect(null, mapDispatchToProps)(Login);
