import React from 'react';
import {connect} from 'react-redux';
import {userLoginFetch} from '../../../actions/loginAction';
import '../../../styles/Login.css';
import {Redirect} from 'react-router-dom';

class Login extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: "",
      password: ""
    }
  }

  handleSubmit = event => {
    event.preventDefault()
    userLoginFetch(this.state.username,this.state.password)
  }
  LoginSuccess(){
    localStorage.setItem('login','success')
    return <Redirect to="/homepage"/>
  }

  render() { 
    return ( 
    <>
      <div className="login_space">
      <div className="login_page">
        <div className="form">
        <div className="ad_logo">
          <h2 className="font-italic">Ad setting</h2>
        </div>
          <form className="login_form" onSubmit={this.handleSubmit}>
            <label>User name:</label>
            <input type="text" placeholder="username" onChange={(event) => { this.setState({ username: event.target.value }) }}/>
            <label>Password:</label>
            <input type="password" placeholder="password" onChange={(event) => { this.setState({ password: event.target.value }) }} />
            <button>login</button>

          </form>
        </div>
      </div>
      </div>

      </> );
  }
}
const mapStateToProps =(state) =>{
  return {
    // isLoginPending: state.loginReducer.isLoginPending,
    // isLoginSuccess: state.loginReducer.isLoginSuccess,
    // loginError: state.loginReducer.loginError,
  }
};
const mapDispatchToProps =(dispatch) =>{
  return {
    login: (username, password) => dispatch(userLoginFetch(username,password))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
