import axios from 'axios';

 export const userLoginFetch = (username, password) => {

    return axios({
        method: "POST",
        url: "http://localhost:8000/auth/login/",
        data: {
          username: username,
          password: password
        }
      }).then(
        response =>{
          const token= response.data.access_token
          localStorage.setItem("access_token", token)
          console.log(token)
          console.log(username)
        })
    }
