import React from 'react';
import {connect} from 'react-redux';
// import {userLoginFetch} from '../../../actions/loginAction';
import '../../../styles/Login.css';
// import {Redirect} from 'react-router-dom';
import {userActions} from '../../../actions/user.action';

class Login extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: "",
      password: "",
      submitted: false
    }
  }

  handleSubmit = event => {
    event.preventDefault()
    this.setState({submitted: true});
    const {username, password} = this.state;
    const {dispatch} = this.props;
    if(username &&password){
      dispatch(userActions.login(username,password));
    }
  }

  render() { 
    const {loggingIn} = this.props;
    const {username, password, submitted} = this.state;
    return ( 
    <>
      <div className="login_space">
      <div className="login_page">
        <div className="form">
        <div className="ad_logo">
          <h2 className="font-italic">Ad setting</h2>
        </div>
          <form className="login_form" onSubmit={this.handleSubmit}>
          <div className={ "form-group"+ (submitted && !username ? ' has-error' : '')}>
          <label htmlFor="username">User name:</label>
          <input type="text" name="username" value={username} placeholder="username" onChange={(event) => { this.setState({ username: event.target.value }) }}/>
          {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
          </div>
          <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
          <label htmlFor="password">Password:</label>
            <input type="password" name= "password" placeholder="password" onChange={(event) => { this.setState({ password: event.target.value }) }} />
            {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
          </div>
          <div> 
          <button>login</button>
          {loggingIn &&
            <img src="data:image/gif;base64," />
            }
          </div>


          </form>
        </div>
      </div>
      </div>

      </> );
  }
}
const mapStateToProps =(state) =>{
  const {loggingIn} = state.authentication;
  return {
    loggingIn
  }
};

// const connectedLoginPage = connect(mapStateToProps)(Login);
// const mapDispatchToProps =(dispatch) =>{
//   return {
//     login: (username, password) => dispatch(userLoginFetch(username,password))
//   }
// }

export default connect(mapStateToProps)(Login);
