import React from 'react';
// import {connect} from 'react-redux';
// import {userLoginFetch} from '../../../actions/loginAction';
import axios from 'axios';
import '../../../styles/Login.css';
import {Link} from 'react-router-dom';

class Login extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: "",
      password: ""
    }
    this.handleClick = this.handleClick.bind(this);
  }
  
 
  handleClick ()  {
    axios({
      method: "POST",
      url: "http://localhost:8000/auth/login/",
      data: {
        username: this.state.username,
        password: this.state.password
      }
    }).then(
      response =>{
        const token= response.data.access_token
        localStorage.setItem("access_token", token)
        console.log(token)
        console.log(this.state.username)
      }
    ).catch(error=>{
      console.log(error)
  })
  
  }
  render() { 
    return ( 
    <>
      <div className="login_space">
      <div className="login_page">
        <div className="form">
        <div className="ad_logo">
          <h2 className="font-italic">Ad setting</h2>
        </div>
          <form className="login_form">
            <label>User name:</label>
            <input type="text" placeholder="username" onChange={(event) => { this.setState({ username: event.target.value }) }}/>
            <label>Password:</label>
            <input type="password" placeholder="password" onChange={(event) => { this.setState({ password: event.target.value }) }} />
            <Link to='/homepage'>
            <button onClick={this.handleClick}>login</button>
            </Link>

          </form>
        </div>
      </div>
      </div>
      
      
      </> );
  }
}
export default Login;
