import React from 'react';
import {connect} from 'react-redux';
import {userLoginFetch} from '../../../actions/loginAction';
import axios from 'axios';
import '../../../styles/Login.css';
import {Link, Redirect} from 'react-router-dom';

class Login extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: "",
      password: ""
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  
 
  handleSubmit(e)  {
  //   axios({
  //     method: "POST",
  //     url: "http://localhost:8000/auth/login/",
  //     data: {
  //       username: this.state.username,
  //       password: this.state.password
  //     }
  //   }).then(
  //     response =>{
  //       const token= response.data.access_token
  //       localStorage.setItem("access_token", token)
  //       console.log(token)
  //       console.log(this.state.username)
  //     }
      
  //   ).catch(error=>{
  //     console.log(error)
  // })
  e.preventDefault();
  let {username, password}= this.state;
  this.props.userLoginFetch(username,password);
  this.setState({
    username: "",
    password: ""
  });
  }
  LoginSuccess(){
    localStorage.setItem('login','success')
    return <Redirect to="/homepage"/>
  }

  render() { 
    return ( 
    <>
      <div className="login_space">
      <div className="login_page">
        <div className="form">
        <div className="ad_logo">
          <h2 className="font-italic">Ad setting</h2>
        </div>
          <form className="login_form" onSubmit={this.handleSubmit}>
            <label>User name:</label>
            <input type="text" placeholder="username" onChange={(event) => { this.setState({ username: event.target.value }) }}/>
            <label>Password:</label>
            <input type="password" placeholder="password" onChange={(event) => { this.setState({ password: event.target.value }) }} />
            <div className="message">  
                            { isLoginSuccess && this.LoginSuccess() }
                            { loginError && <p style={i}>Check PassWord and Username</p> }
                            {!isLoginSuccess}
                          </div>
            
            <button>login</button>

          </form>
        </div>
      </div>
      </div>
      
      </> );
  }
}
const mapStateToProps =(state) =>{
  return {
    isLoginPending: state.userLoginFetch.isLoginPending,
    isLoginSuccess: state.userLoginFetch.isLoginSuccess,
    loginError: state.userLoginFetch.loginError,
  }
};
const mapDispatchToProps =(dispatch) =>{
  return {
    login: (username, password) => dispatch(userLoginFetch(username,password))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
